mapper.update:
update grouphub.rate
set rateTitle = #{rateTitle}, rateScore = #{rateScore}, rateCreateTime = #{rateCreateTime}, rateContent = #{rateContent}, rateTotalReply=#{rateTotalReply}
where rateId = #{rateId};

update grouphub.rate
set rateTotalReply = rateTotalReply + 1
where rateId = #{rateId};

update grouphub.rate
set rateTotalReply = 0
where rateId = #{rateId};
